<?phpswitch(isset($_GET['ac'])? $_GET['ac']: 'php'){	case 'sugerir':					if(!empty($_POST['dataIni']))			$timeInt = jf_dunix($_POST['dataIni']);				if(!empty($_POST['dataFim']))			$timeFim = jf_dunix($_POST['dataFim']);						if((isset($timeFim) && $timeInt > $timeFim) || !isset($timeInt))			$erro[] = 'datinv';		if($_POST['nome'] == '')			$erro[] = 'nominv';				if(strlen($_POST['descricao']) < 50)			$erro[] = 'desins';				if($_POST['local'] == '')			$erro[] = 'locinv';					if(!isset($erro)){					$insert['criador'] = $_SESSION['sh']['id'];			$insert['dataInt'] = $timeInt;			$insert['dataFim'] = $timeFim;			$insert['nome'] = $_POST['nome'];			$insert['descricao'] = $_POST['descricao'];			$insert['local'] = $_POST['local'];			$insert['inserido'] = time();						if ($teste = jf_insert(PREFIXO . 'evento', $insert))				$erro[] = $teste;					$insert = null;						$insert['evento'] = mysql_insert_id();			$insert['comunidade'] = $_GET[1];						if ($teste = jf_insert(PREFIXO . 'evento_comunidade', $insert))				$erro[] = $teste;					}else{			$_SESSION['sugestao'] = $_POST;		}				header('Location: ' . $shHome . $_GET[1] . '/eventos' . (isset($erro)? '/sugerir/erro=' . implode(',', $erro):''));		break;		case 'del':			$sql = '			SELECT				id			FROM				' . PREFIXO . 'usuario_comunidade							WHERE				usuario = "' . $_SESSION['sh']['id'] . '" and comunidade = "' . $_GET[1] . '"		';				$sql = mysql_query($sql);				if(mysql_num_rows($sql) >= 1){						$delite['evento'] = $_GET['evi'];			$delite['comunidade'] = $_GET[1];						if($erro = jf_delete(PREFIXO . 'evento_comunidade', $delite))				echo $erro;				}				header('Location: ' . $shHome . $_GET[1] . '/eventos');			break;	}?>