<?phpswitch(isset($_GET['ac'])? $_GET['ac']: 'php'){	case 'init':		$_SESSION['comu']['url'] = $_POST['ret'];				//echo '<pre>' . print_r($_SESSION, true) . '</pre>';				header('location: ' . $shHome . $cmdd['id'] . '/imagem');	break;	case 'subimg':			require_once($shdir . 'functions/cortaImgPerfil.php');		//definindo nome da imagem		$imagemNome = explode('.', $_FILES['img']['name']);		$extenc = array_pop($imagemNome);		$imagemNome = jf_formata_url($cmdd['nome']) . '_' . substr(md5(time()), rand(0, 24), 8) . '.' . $extenc;		//cortando a imagem		move_uploaded_file($_FILES['img']['tmp_name'], '../uploads/tmp/original_' . $imagemNome);		cortaImgPerfil('../uploads/tmp/original_' . $imagemNome, 161, 149, '../uploads/tmp/g_' . $imagemNome);		cortaImgPerfil('../uploads/tmp/g_' . $imagemNome, 100, 100, '../uploads/tmp/m_' . $imagemNome);		cortaImgPerfil('../uploads/tmp/g_' . $imagemNome, 50, 50, '../uploads/tmp/mini_' . $imagemNome);		$_SESSION['comu']['img'] = $imagemNome;		header('location: ' . $shHome . $cmdd['id'] . '/imagem');	break;	case 'imgconf':		if (isset($_SESSION['comu']['img'])){			@unlink('../uploads/comunidades/original_' . $cmdd['img']);			@unlink('../uploads/comunidades/g_' . $cmdd['img']);			@unlink('../uploads/comunidades/m_' . $cmdd['img']);			@unlink('../uploads/comunidades/mini_' . $cmdd['img']);			rename(				'../uploads/tmp/original_' . $_SESSION['comu']['img'],				'../uploads/comunidades/original_' . $_SESSION['comu']['img']			);			rename(				'../uploads/tmp/g_' . $_SESSION['comu']['img'],				'../uploads/comunidades/g_' . $_SESSION['comu']['img']			);			rename(				'../uploads/tmp/m_' . $_SESSION['comu']['img'],				'../uploads/comunidades/m_' . $_SESSION['comu']['img']			);			rename(				'../uploads/tmp/mini_' . $_SESSION['comu']['img'],				'../uploads/comunidades/mini_' . $_SESSION['comu']['img']			);			$update['img'] = $_SESSION['comu']['img'];			if ($erro = jf_update(PREFIXO . 'comunidade', $update, array('id' => $cmdd['id'])))				echo $erro;			unset($_SESSION['comu']['img']);		}		$url = $UrlReal . $_SESSION['comu']['url'];		unset($_SESSION['comu']['url']);		header('location: ' . $url);		break;	case 'imgcans':		if (isset($_SESSION['comu']['img'])){			unlink('../uploads/tmp/original_' . $_SESSION['comu']['img']);			unlink('../uploads/tmp/g_' . $_SESSION['comu']['img']);			unlink('../uploads/tmp/m_' . $_SESSION['comu']['img']);			unlink('../uploads/tmp/mini_' . $_SESSION['comu']['img']);			unset($_SESSION['comu']['img']);		}				echo '<pre>' . print_r($_SESSION, true) . '</pre>';		$url = $UrlReal . $_SESSION['comu']['url'];		unset($_SESSION['comu']['url']);		header('location: ' . $url);	break;	}?>