<?phprequire_once($shdir . 'functions/geraInput.php');require_once($shdir . 'functions/rd_paginacao.php');$wherePes = '';if (isset($_GET['pes'])){	$_GET['pes'] = urldecode($_GET['pes']);		$pes = explode('+', $_GET['pes']);		$wherePes .= ' and (';		foreach($pes as $chave => $valor)		$wherePes .= ($chave == 0? '': ' and ') . ' ((b.nome LIKE "%' . $valor . '%" ) or (b.sobrenome LIKE "%' . $valor . '%" ))';		$wherePes .= ')';}$paginacao = new rd_paginacao();$paginacao->visivel = 16;$paginacao->atual = isset($_GET['pag'])? $_GET['pag']: 1;$paginacao->url = jf_monta_link($_GET, 'pag', JF_URL_AMIGAVEL) . '/pag=';$sql = '	SELECT		a.id as id_l, b.id, b.nome, b.img, if(b.trabalhoCargo != "" and b.trabalhoOnde != "", concat(b.trabalhoCargo, ", ", b.trabalhoOnde), if(b.formacaoOque != "" and b.formacaoOnde != "", concat(b.formacaoOque, ", ", b.formacaoOnde), if(b.cidade != "", concat(b.cidade, "/", b.estado), (select nome from br_estado where sigla = b.estado)))) as descricao	FROM		' . PREFIXO . 'usuario_comunidade a		LEFT JOIN			' . PREFIXO . 'usuario_conta b		ON			b.id = a.usuario	WHERE		a.comunidade = "' . $cmdd['id'] . '" and b.id != "'.$_SESSION['sh']['id'].'" and status !=  "E" ' . $wherePes . '';$paginacao->total = ceil((mysql_num_rows(mysql_query($sql))) / $paginacao->visivel);$paginacao->estrutura = array(	array(		'type'  => 'first',		'value' => _t('ac-primeira', false)	),	array(		'type'  => 'number',		'value' =>	3	),	array(		'type'  => 'last',		'value' => _t('ac-ultima', false)	));$sql .= '	ORDER BY		b.nome ASC		LIMIT		' . $paginacao->limit() . '	';$contatos = mysql_query($sql);$busca = null;if (isset($_GET['pes']))	$busca = array('pes' => str_replace('+', ' ', $_GET['pes']));?><div id="contatos" class="pagina comMenu">		<div class="cabecalho">		<h1 class="shcolor"><?php _t('cmd-membros');?></h1>				<div class="ferramentas">			<form action="onserver/comunidade/<?php echo $cmdd['id']?>/membros/ac=pes" method="post" class="pes">				<div>					<?php 										$busca = '';					$class = '';							if(isset($_GET['pes'])){ 						$_GET['pes'] = urldecode($_GET['pes']);						$busca = $_GET['pes'];						$class = ' pesCont';						echo '<a href="'.jf_monta_link($_GET, 'pes', JF_URL_AMIGAVEL).'"><img src="',$shdir,'imagens/clean.png" alt=""/></a>';					} 							echo geraInput('pes', $busca, array('class' => 'input'.$class, 'rel' => _t('cmb-enct-contato', false)));					?>				</div>								<span class="botao shbackground"><button type="submit"><?php _t('ac-pesquisar'); ?></button></span>			</form>			<span class="botao shbackground right"><a  href="<?php echo $reveni; ?>"><?php _t('ac-voltar'); ?></a></span>						<?php echo ((isset($_GET['pes']))? '<span class="pesr segoe">'._t('ac-resultados', false).' "<em>' . str_replace('+', ' ', $_GET['pes']) . '</em>"</span>': ''); ?>		</div>			</div>		<div class="contatos">			<div class="meusContatos">						<?php			if(mysql_num_rows($contatos) == 0)				echo '<span class="nao_encontrou">'. _t('ac-nao-encontrou', false) . '</span>';						while($contato = mysql_fetch_assoc($contatos)){?>					<div class="contato">								<div class="img">									<a href="perfil/<?php echo $contato['id']?>">											<img src="<?php echo img('uploads/contas/normal_' . $contato['img']);?>" />										</a>								</div>							<div class="informacaoes">									<span class="nome">												<a href="perfil/<?php echo $contato['id']?>"><?php echo stripcslashes($contato['nome'])?></a>					</span>										<span class="descricao">						<a href="perfil/<?php echo $contato['id']?>"><?php echo stripcslashes($contato['descricao'])?></a>					</span>								<div class="acoes">						<a class="acao mensagem" href="onserver/social/mensagens/ac=mensagem/contato=<?php echo $contato['id']?>"></a>						<?php						if($cmdd['adm'])							echo '<a class="acao delete" href="', $shOnserver, $cmdd['id'], '/membros/ac=estingi/m=', $contato['id_l'], '"></a>';						?>					</div>								</div>							</div>						<?php			}?>				</div>		</div>		<?php	$paginacao->montar();?></div><script src="<?php echo $UrlReal , $shdir; ?>js/jquery.jf_inputext.js" type="text/javascript"></script><script>	$('.delete').click(function(){		if(!confirm("Deseja apagar este Mensagem")){			return false;		}	});	$('.input').jf_inputext();</script>